func LookUp(lic string) (LicenseMeta, bool) {
	if meta, ok := spdxLicenseList[strings.ToLower(lic)]; ok {
		return meta, true
	}
	return meta{}, false
}

func FreeForAnyUse(lic string) bool {
	if _, ok := freeAnyUser[strings.ToLower(lic)]; ok {
		return true
	}
	return false
}

